	function popup(message) {					// get the screen height and width  		var maskHeight = jQuery(document).height();  		var maskWidth = jQuery(window).width();				var dialogHeight = jQuery(window).height();  				// calculate the values for center alignment		var dialogTop =  (dialogHeight/2) - (jQuery('#dialog-box').height());  		var dialogLeft = (maskWidth/2) - (jQuery('#dialog-box').width()/2); 				// assign values to the overlay and dialog box		jQuery('#dialog-overlay').css({height:maskHeight, width:maskWidth}).show();		jQuery('#dialog-box').css({top:dialogTop, left:dialogLeft}).show();				// display the message		jQuery('#dialog-message').html(message);					}			jQuery(document).ready(function($){		$('#dialog-overlay, #dialog-box').live('click', function () {					$('#dialog-overlay, #dialog-box').hide();									return false;		});				// if user resize the window, call the same function again		// to make sure the overlay fills the screen and dialogbox aligned to center			$(window).resize(function () {						//only do it if the dialog box is not hidden			if (!$('#dialog-box').is(':hidden')) popup();				});					var html = '<div id="dialog-overlay"></div><div id="dialog-box"><div class="dialog-content"><div id="dialog-message"></div><a href="#" class="button">Close</a></div></div>';		$('body').prepend(html);					});